// src/components/ProjectCard/ProjectCard.js
import React from 'react';
import { ethers } from 'ethers';
import './ProjectCard.css';

function ProjectCard({ id, name, category, description, projectDDL, fundingGoal, amountRaised, status, milestones, onClick }) {
  const daysUntilDeadline = (new Date(projectDDL) - new Date()) / (1000 * 60 * 60 * 24);
  const isDeadlineSoon = daysUntilDeadline <= 6;

  const formatDescription = (text) => {
    const maxLength = 300;
    if (text.length > maxLength) {
      return text.substring(0, maxLength - 3) + '...';
    }
    return text;
  };

  const formatTitle = (text) => {
    const maxLength = 30; // Adjusted max length for title
    if (text.length > maxLength) {
      return text.substring(0, maxLength - 3) + '...';
    }
    return text;
  };

  const displayedDescription = formatDescription(description);
  const displayedTitle = formatTitle(name);

  const fundingGoalInETH = ethers.utils.formatEther(fundingGoal);
  const amountRaisedInETH = ethers.utils.formatEther(amountRaised);

  return (
    <div className="project-card" onClick={onClick}>
      <h3 className="project-name">{displayedTitle}</h3>
      <p className="project-id"><strong>ID:</strong> {id}</p>
      <p className="project-category"><strong>Category:</strong> {category}</p>
      <p>
        <strong>Project Description:</strong>
        <span className="project-description"> {displayedDescription}</span>
      </p>
      <p className="project-ddl">
        <strong>Project Deadline: </strong>
        <span className={`deadline-date ${isDeadlineSoon ? 'soon' : ''}`}><strong>
        {new Date(projectDDL).toLocaleDateString()}</strong>
        </span>
      </p>

      {status === 'active' ? (
        <>
          <p><strong>Currently Fundraising</strong> - Funding Goal: {fundingGoalInETH} ETH</p>
          <div className="progress-bar">
            <div className="progress" style={{ width: '100%', backgroundColor: 'green' }}></div>
          </div>
        </>
      ) : (
        <>
          <div className="milestones">
            {milestones.map((milestone, index) => (
              <div key={milestone.milestoneId} className="milestone-progress">
                <div
                  className={`milestone-bar ${milestone.milestonestatus === 'completed' ? 'completed' : index === milestones.findIndex(ms => ms.milestonestatus !== 'completed') ? 'active' : ''}`}
                  style={{ width: `${100 / milestones.length}%` }}
                ></div>
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
}

export default ProjectCard;
