import React, { useState } from 'react';
import MetaMaskInfo from '../MetaMaskInfo/MetaMaskInfo';
import { FaSearch } from 'react-icons/fa';
import './Navbar.css';

function Navbar({ onCreateProject, onShowAllProjects, onShowMyProjects, onShowFundedProjects, onSearch, activePage, onAddressChange }) {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
    onSearch(e.target.value);
  };

  const isActivePage = (page) => {
    if (activePage.startsWith('projectDetails')) {
      return page === 'allProjects' || page === 'myProjects' || page === 'fundedProjects';
    }
    return activePage === page;
  };

  return (
    <nav className="navbar">
      <div className="navbar-top">
        <div className="navbar-left">
          <img src="/logo.png" alt="Logo" className="navbar-logo" />
          <span className="navbar-title">InnoFund</span>
        </div>
        <div className="navbar-right">
          {activePage !== 'createProject' && (
            <div className="search-container">
              <input
                type="text"
                placeholder="Search..."
                value={searchTerm}
                onChange={handleSearchChange}
              />
              <FaSearch className="search-icon" />
            </div>
          )}
          <MetaMaskInfo onAddressChange={onAddressChange} />
        </div>
      </div>
      <div className="navbar-bottom">
        <button className={isActivePage('allProjects') ? 'active' : ''} onClick={onShowAllProjects}>All Projects</button>
        <button className={isActivePage('myProjects') ? 'active' : ''} onClick={onShowMyProjects}>My Projects</button>
        <button className={isActivePage('fundedProjects') ? 'active' : ''} onClick={onShowFundedProjects}>Funded Projects</button>
        <button className={isActivePage('createProject') ? 'active' : ''} onClick={onCreateProject}>Create Project</button>
      </div>
    </nav>
  );
}

export default Navbar;
