// src/components/ProjectDetails/ProjectDetails.js
import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import FundProject from '../FundProject/FundProject';
import UserUploadProof from '../UserUploadProof/UserUploadProof';
import './ProjectDetails.css';

function ProjectDetails({ project, onClose }) {
  const [milestones, setMilestones] = useState([]);
  const [currentUser, setCurrentUser] = useState('');
  const [canUploadProof, setCanUploadProof] = useState(false);

  const fetchCurrentUser = async () => {
    try {
      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      setCurrentUser(accounts[0].toLowerCase());
    } catch (error) {
      console.error('Error fetching current user:', error);
    }
  };

  const fetchMilestones = useCallback(async (projectId) => {
    try {
      const response = await axios.get(`/api/milestones`, {
        params: { projectId }
      });
      const projectMilestones = response.data;

      setMilestones(projectMilestones);

      const hasPendingMilestone = projectMilestones.some(
        (milestone) => milestone.milestonestatus === 'pending'
      );

      setCanUploadProof(
        project.creator.toLowerCase() === currentUser && project.status.toLowerCase() === 'funded' && hasPendingMilestone
      );
    } catch (error) {
      console.error('Error fetching milestones:', error);
    }
  }, [currentUser, project.creator, project.status]);

  useEffect(() => {
    fetchCurrentUser();
    if (project.status.toLowerCase() === 'funded' || project.status.toLowerCase() === 'active') {
      fetchMilestones(project.projectId);
    }
  }, [fetchMilestones, project]);

  const completedMilestones = milestones.filter(milestone => milestone.milestonestatus === 'approved');

  const amountRaisedInEther = parseFloat(project.amountRaised);
  const fundingGoalInEther = project.fundingGoal / 10 ** 18;
  const fundingProgress = (amountRaisedInEther / fundingGoalInEther) * 100;

  return (
    <div className="project-details-container">
      <div className="project-details">
        <button onClick={onClose} className="back-button">Back</button>
        <h2>{project.projectName}</h2>
        <p><strong>ID:</strong> {project.projectId}</p>
        <p><strong>Type:</strong> {project.projectType}</p>
        <p><strong>Description:</strong> {project.projectDescription}</p>
        <p><strong>Project Deadline:</strong> {new Date(project.projectDDL).toLocaleDateString()}</p>

        {project.status.toLowerCase() === 'active' ? (
          <div className="status-section">
            <p className="project-status">Crowdfunding Project - Raised: {amountRaisedInEther.toFixed(2)} / {fundingGoalInEther.toFixed(2)} ETH</p>
            <div className="progress-bar">
              <div className="progress" style={{ width: `${fundingProgress}%` }}></div>
            </div>
            {!project.isCreator && <FundProject projectId={project.projectId} />}
          </div>
        ) : (
          <div className="milestone-section">
            <h3>Milestones</h3>
            <ul>
              {completedMilestones.map((milestone, index) => (
                <li key={`${project.projectId}-${milestone.milestoneId}-${index}`}>
                  <p><strong>Milestone ID:</strong> {milestone.milestoneId + 1}</p>
                  <p><strong>Description:</strong> {milestone.milestoneDescription}</p>
                  <p><strong>Document:</strong> <a href={milestone.otherDocuments} target="_blank" rel="noopener noreferrer">View</a></p>
                </li>
              ))}
            </ul>
          </div>
        )}
        {canUploadProof && <UserUploadProof projectId={project.projectId} />}
      </div>
    </div>
  );
}

export default ProjectDetails;
