import React, { useState } from 'react';
import MetaMaskInfo from '../MetaMaskInfo/MetaMaskInfo';
import { FaSearch } from 'react-icons/fa';  // 导入放大镜图标
import './Navbar.css';

function Navbar({ onCreateProject, onShowAllProjects, onShowMyProjects, onShowFundedProjects, onSearch, activePage, onAddressChange }) {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
    onSearch(e.target.value);
  };

  const getButtonClassName = (page) => {
    return activePage === page ? 'active' : '';
  };

  const isButtonDisabled = (page) => {
    return activePage === page || (activePage === 'projectDetails' && ['allProjects', 'myProjects', 'fundedProjects'].includes(page));
  };

  return (
    <nav className="navbar">
      <div className="navbar-top">
        <div className="navbar-left">
          <img src="/logo.png" alt="Logo" className="navbar-logo" />
          <span className="navbar-title">InnoFund</span>
        </div>
        <div className="navbar-right">
          {activePage !== 'createProject' && (
            <div className="search-container">
              <input
                type="text"
                placeholder="Search..."
                value={searchTerm}
                onChange={handleSearchChange}
              />
              <FaSearch className="search-icon" />
            </div>
          )}
          <MetaMaskInfo onAddressChange={onAddressChange} />
        </div>
      </div>
      <div className="navbar-bottom">
        <button 
          className={getButtonClassName('allProjects')}
          onClick={onShowAllProjects} 
          disabled={isButtonDisabled('allProjects')}
        >
          All Projects
        </button>
        <button 
          className={getButtonClassName('myProjects')}
          onClick={onShowMyProjects} 
          disabled={isButtonDisabled('myProjects')}
        >
          My Projects
        </button>
        <button 
          className={getButtonClassName('fundedProjects')}
          onClick={onShowFundedProjects} 
          disabled={isButtonDisabled('fundedProjects')}
        >
          Funded Projects
        </button>
        <button 
          className={getButtonClassName('createProject')}
          onClick={onCreateProject} 
          disabled={isButtonDisabled('createProject')}
        >
          Create Project
        </button>
      </div>
    </nav>
  );
}

export default Navbar;
